mapper.select:
select


from t_blog
where  recommend = true order by update_time desc  limit 0,#{size,jdbcType=INTEGER}

select

from t_blog
order by update_time desc  limit #{cpage,jdbcType=INTEGER},#{size,jdbcType=INTEGER}

select

from t_blog
where  1=1

and id= #{id,jdbcType=BIGINT}


and appreciation=#{appreciation,jdbcType=BIT}


and commentabled =#{commentabled,jdbcType=BIT}


and create_Time= #{createTime,jdbcType=TIMESTAMP}


and description= #{description,jdbcType=VARCHAR}


and first_Picture= #{firstPicture,jdbcType=VARCHAR}


and flag=#{flag,jdbcType=VARCHAR}


and published=#{published,jdbcType=BIT}


and recommend=#{recommend,jdbcType=BIT}


and share_Statement= #{shareStatement,jdbcType=BIT}


and title  like concat('%',#{title,jdbcType=VARCHAR},'%')



and update_Time=#{updateTime,jdbcType=TIMESTAMP}


and views=#{views,jdbcType=INTEGER}


and type_Id=#{typeId,jdbcType=BIGINT}


and user_Id= #{userId,jdbcType=BIGINT}


and content like concat('%',#{content,jdbcType=VARCHAR},'%')


and exists(select blogs_id from t_blog_tags where tags_id=#{tagsId,jdbcType=BIGINT})

select
count(1)
from t_blog t where 1=1

  and t.id= #{id,jdbcType=BIGINT}


  and t.ppreciation=#{appreciation,jdbcType=BIT}


  and t.commentabled =#{commentabled,jdbcType=BIT}


  and t.create_Time= #{createTime,jdbcType=TIMESTAMP}


  and t.description= #{description,jdbcType=VARCHAR}


  and t.first_Picture= #{firstPicture,jdbcType=VARCHAR}


  and t.flag=#{flag,jdbcType=VARCHAR}


  and t.published=#{published,jdbcType=BIT}


  and t.recommend=#{recommend,jdbcType=BIT}


  and t.share_Statement= #{shareStatement,jdbcType=BIT}


  and t.title like concat('%',#{title,jdbcType=VARCHAR},'%')


  and t.update_Time=#{updateTime,jdbcType=TIMESTAMP}


  and t.views=#{views,jdbcType=INTEGER}


  and t.type_Id=#{typeId,jdbcType=BIGINT}


  and t.user_Id= #{userId,jdbcType=BIGINT}


  and t.content like concat('%',#{content,jdbcType=VARCHAR},'%')


  and exists(select blogs_id from t_blog_tags where tags_id=#{tagsId,jdbcType=BIGINT})
